def test_multiplication_table(m):
    score = 0
    count = 0
    for i in range(1, 11):
        while True:
            answer = input(f"{m} x {i} = ")
            if answer.isdigit():
                answer = int(answer)
                count += 1
                break
            else:
                print("Please enter a valid number!")

        while answer != m * i:
            answer = input(f"Incorrect. Please try again. {m} x {i} = ?: ")
            if answer.isdigit():
                answer = int(answer)
                count += 1
            else:
                print("Please enter a valid number!")

        print("Correct!")
        score += 1

    return int(score / count * 100)

